import React, {useEffect, useState} from 'react';
import axios from "axios";
import MovieComponent from "./MovieComponent";
import MovieI from "../models/Movie";

const FetchMovies = () => {

    const [data, setData] = useState([]);
    const [genres, setGenres] = useState([]);

    useEffect(() => {
        const fetchMovies = async () => {
            try {
                const response = await axios.get('https://api.themoviedb.org/3/movie/popular', options);
                setData(response.data.results);
            } catch (err) {
                console.error('Erreur lors de la récupération des films :', err);
            }
        };

        const fetchGenres = async () => {
            try {
                const response = await axios.get('https://api.themoviedb.org/3/genre/movie/list', options);
                setGenres(response.data.genres);
            } catch (err) {
                console.error('Erreur lors de la récupération des catégories :', err);
            }
        };

        const options = {
            method: 'GET',
            headers: {
                accept: 'application/json',
                Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiIzZWVjZTA4MDU3ZmVhMjRkNzI1ZGMyMTc1ZWZmOGY4NSIsInN1YiI6IjY0YzM2NmRkNDMyNTBmMDBlODA0NWFlMyIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.fz8Zd74v5UOGKpBtBIdhelLPKM6ntHVrGQNS1yBU1kk'
            }
        };

        fetchMovies();
        fetchGenres();
    }, []);

    genres.forEach((genre:Genre)=> {
        const filmsWithGenres = () => data.filter((movie:Movie) => {
            if (movie.genres_ids.includes(genre.id)) {
                console.log('Film avec la catégorie', genre.name, ':', filmsWithGenres)
                return true;
            } else {
                return false;
            }
        });
    })

    return (
        <div>
            <ul>
                {
                    data.map((movie: Movie) => (
                        <MovieComponent key={movie.id} movie={movie} />
                    ))
                }
            </ul>
        </div>
    );
};

export default FetchMovies;
