import React, {useEffect, useState} from 'react';
import axios from "axios";
import MovieComponent from "./MovieComponent";
import { Movie } from "../models/Movie";
import { Genre } from "../models/Genre";

const FetchMovies = () => {

    const [data, setData] = useState<Movie[]>([]);
    const [genres, setGenres] = useState<Genre[]>([]);

    useEffect(() => {
        const fetchMovies = async () => {
            try {
                const resData = await axios.get('https://api.themoviedb.org/3/movie/popular', options);
                setData(resData.data.results);
            } catch (err) {
                console.error('Erreur lors de la récupération des films :', err);
            }
        };

        const fetchGenres = async () => {
            try {
                const resGenres = await axios.get('https://api.themoviedb.org/3/genre/movie/list', options);
                setGenres(resGenres.data.genres);
            } catch (err) {
                console.error('Erreur lors de la récupération des catégories :', err);
            }
        };

        const options = {
            method: 'GET',
            headers: {
                accept: 'application/json',
                Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiIzZWVjZTA4MDU3ZmVhMjRkNzI1ZGMyMTc1ZWZmOGY4NSIsInN1YiI6IjY0YzM2NmRkNDMyNTBmMDBlODA0NWFlMyIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.fz8Zd74v5UOGKpBtBIdhelLPKM6ntHVrGQNS1yBU1kk'
            }
        };

        fetchMovies().then(fetchGenres);

        genres.forEach((genre:Genre)=> {
            console.log("coucou")
            data.filter((movie:Movie) => {
                if (movie.genres_ids.includes(genre.id)) {
                    movie.genres.push(genre);
                    console.log(movie.genres)
                } else {
                    console.log('rien de bon');
                }
            });
        })

    }, [data, genres]);

    return (
        <div>
            <ul>
                {
                    data.map((movie: Movie) => (
                        <MovieComponent key={movie.id} movie={movie} />
                    ))
                }
            </ul>
        </div>
    );
};

export default FetchMovies;
