import React, {useEffect, useState} from 'react';
import axios from "axios";
import MovieComponent from "./MovieComponent";
// import { Movie, Category } from './FetchMovies';

export interface Movie {
    id:number;
    original_title:string;
    poster_path:string;
    genres_ids:number[];
    overview:string;
    vote_average:number;
    vote_count:number;
    categories?:Category[];
}

export interface Category {
    id:number;
    name:string;
}

const FetchMovies = () => {

    const mapGenresToMovies = (movies: Movie[], categories: Category[]): Movie[] => {
        return movies.map(movie => {
            const movieCategories = movie.genres_ids.map(genreId =>
                genres.find(category => category.id === categoryId)
            );
            return { ...movie, categories: movieCategories };
        });
    };

    const [data, setData] = useState([]);
    const [genres, setGenres] = useState([]);

    useEffect(() => {
        const fetchMovies = async () => {
            try {
                const response = await axios.get('https://api.themoviedb.org/3/movie/popular', options);
                setData(response.data.results);
            } catch (err) {
                console.error('Erreur lors de la récupération des films :', err);
            }
        };

        const fetchCategories = async () => {
            try {
                const response = await axios.get('https://api.themoviedb.org/3/genre/movie/list', options);
                setGenres(response.data.genres);
            } catch (err) {
                console.error('Erreur lors de la récupération des catégories :', err);
            }
        };

        const options = {
            method: 'GET',
            headers: {
                accept: 'application/json',
                Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiIzZWVjZTA4MDU3ZmVhMjRkNzI1ZGMyMTc1ZWZmOGY4NSIsInN1YiI6IjY0YzM2NmRkNDMyNTBmMDBlODA0NWFlMyIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.fz8Zd74v5UOGKpBtBIdhelLPKM6ntHVrGQNS1yBU1kk'
            }
        };

        fetchMovies();
        fetchCategories()
    }, []);

    return (
        <div>
            <ul>
                {
                    mapGenresToMovies(data, genres).map((movie: Movie) => (
                        <MovieComponent key={movie.id} movie={movie} />
                    ))
                }
            </ul>
        </div>
    );
};

export default FetchMovies;
