import React from 'react';
import {Movie} from "./FetchMovies";
import GenreComponent from "./GenreComponent";
import FetchGenres, {Genre} from "./FetchGenres";

type Prop = {
    movie: Movie
    genre: Genre
}
const MovieComponent = ({movie, genre}: Prop) => {



    movie.genres.push(genre.id)

    return (
        <li>
            <img src={movie.backdrop_path} alt={"image du film " + movie.original_title}/>
            <h3>{movie.original_title}</h3>
            <p>{movie.overview}</p>
            <p>{movie.vote_average}/10 ({movie.vote_count} votes)</p>
            <ul>genres :
                {
                    movie.genres.join(", ")
                }
            </ul>
        </li>
    );
};

export default MovieComponent;
