            case histMobile:
                coloredLine.style.left = "33%";
                break;
            case convMobile:
                coloredLine.style.left = "67%";
                break;

            default:
                coloredLine.style.left = "0%";
                break;
        }
    }

    return (
        <nav>
            <ul>
                <li>
